import React, { useState, useRef } from 'react';

function DrawingArea() {
    const [lines, setLines] = useState([]);
    const [currentLine, setCurrentLine] = useState(null);
    const svgRef = useRef(null);

    const startLine = (event) => {
        const svgRect = svgRef.current.getBoundingClientRect();
        const x = event.clientX - svgRect.left;
        const y = event.clientY - svgRect.top;
        setLines([...lines, { x1: x, y1: y, x2: x, y2: y }]);
    };

    const drawLine = (event) => {
        if (!lines.length) return;
        const svgRect = svgRef.current.getBoundingClientRect();
        const x = event.clientX - svgRect.left;
        const y = event.clientY - svgRect.top;
        const newLines = lines.slice(0, -1);
        const lastLine = { ...lines[lines.length - 1], x2: x, y2: y };
        setLines([...newLines, lastLine]);
    };



    const endLine = () => {

        if (!currentLine) return;
            // Add the current line to the list of completed lines
        setLines([...lines, currentLine]);
            // Reset the currentLine to null to start fresh for the next line
        setCurrentLine(null);

        fetch('http://localhost:9000/receive-svg', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ lines })
        })
        .then(response => response.json())
        .then(data => console.log(data.message));
    };

    return (
        <svg
            ref={svgRef}
            onMouseDown={startLine}
            onMouseMove={drawLine}
            onMouseUp={endLine}
            style={{ border: '1px solid black', cursor: 'crosshair' }}
            width="800"
            height="600"
        >
            {lines.map((line, index) => (
                <line key={index} {...line} stroke="black" />
            ))}
        </svg>
    );
}

export default DrawingArea;
